import { CommonModule } from '@angular/common';
import { Component } from '@angular/core';
import { FormsModule } from '@angular/forms';

interface BorderExample {
  className: string;
  text: string;
}

interface ClassCategory {
  name: string;
  classes: string[];
}

@Component({
  selector: 'app-borders-demo',
  standalone: true,
  imports: [FormsModule, CommonModule],
  templateUrl: './borders-demo.component.html',
  styleUrl: './borders-demo.component.scss'
})
export class BordersDemoComponent {
  manualClass = '';
  customText = '';
  examples: BorderExample[] = [];
  defaultText = 'Border Example';
  selectedCategory = '';
  selectedClass = '';

  classCategories: ClassCategory[] = [
    {
      name: 'Border Width (Global)',
      classes: [
        'global-bw-none',
        'global-bw-thinner',
        'global-bw-thin',
        'global-bw-thick',
        'global-bw-thicker'
      ]
    },
    {
      name: 'Border Width (Top)',
      classes: [
        'global-bw-t-none',
        'global-bw-t-thinner',
        'global-bw-t-thin',
        'global-bw-t-thick',
        'global-bw-t-thicker'
      ]
