        'global-bw-b-thinner',
        'global-bw-b-thin',
        'global-bw-b-thick',
        'global-bw-b-thicker'
      ]
    },
    {
      name: 'Border Width (Left)',
      classes: [
        'global-bw-l-none',
        'global-bw-l-thinner',
        'global-bw-l-thin',
        'global-bw-l-thick',
        'global-bw-l-thicker'
      ]
    },
    {
      name: 'Border Style',
      classes: [
        'global-bs-solid',
        'global-bs-dashed',
        'global-bs-dotted',
        'global-bs-double',
        'global-bs-none'
      ]
    },
    {
      name: 'Border Radius (Global)',
      classes: [
        'global-br-none',
        'global-br-xsmall',
        'global-br-small',
        'global-br-medium',
        'global-br-large',
        'global-br-xlarge',
        'global-br-2xlarge',
        'global-br-max'
      ]
    },
    {
      name: 'Border Radius (Top Left)',
      classes: [
        'global-br-tl-none',
        'global-br-tl-xsmall',
        'global-br-tl-small',
        'global-br-tl-medium',
        'global-br-tl-large',
        'global-br-tl-xlarge',
        'global-br-tl-2xlarge',
        'global-br-tl-max'
      ]
    },
    {
      name: 'Border Radius (Top Right)',
      classes: [
        'global-br-tr-none',
        'global-br-tr-xsmall',
        'global-br-tr-small',
        'global-br-tr-medium',
        'global-br-tr-large',
        'global-br-tr-xlarge',
        'global-br-tr-2xlarge',
        'global-br-tr-max'
      ]
    },
    {
      name: 'Border Radius (Bottom Left)',
      classes: [
        'global-br-bl-none',
        'global-br-bl-xsmall',
        'global-br-bl-small',
        'global-br-bl-medium',
        'global-br-bl-large',
        'global-br-bl-xlarge',
        'global-br-bl-2xlarge',
        'global-br-bl-max'
      ]
    },
    {
      name: 'Border Radius (Bottom Right)',
      classes: [
        'global-br-br-none',
        'global-br-br-xsmall',
        'global-br-br-small',
        'global-br-br-medium',
        'global-br-br-large',
        'global-br-br-xlarge',
        'global-br-br-2xlarge',
        'global-br-br-max'
      ]
    },
    {
      name: 'Border Radius (Top)',
      classes: [
        'global-br-t-none',
        'global-br-t-xsmall',
        'global-br-t-small',
        'global-br-t-medium',
        'global-br-t-large',
        'global-br-t-xlarge',
        'global-br-t-2xlarge',
        'global-br-t-max'
      ]
    },
    {
      name: 'Border Radius (Bottom)',
      classes: [
        'global-br-b-none',
        'global-br-b-xsmall',
        'global-br-b-small',
        'global-br-b-medium',
        'global-br-b-large',
        'global-br-b-xlarge',
        'global-br-b-2xlarge',
        'global-br-b-max'
      ]
    },
    {
      name: 'Border Radius (Left)',
      classes: [
        'global-br-l-none',
        'global-br-l-xsmall',
        'global-br-l-small',
        'global-br-l-medium',
        'global-br-l-large',
        'global-br-l-xlarge',
        'global-br-l-2xlarge',
        'global-br-l-max'
      ]
