    {
      name: 'Border Radius (Right)',
      classes: [
        'global-br-r-none',
        'global-br-r-xsmall',
        'global-br-r-small',
        'global-br-r-medium',
        'global-br-r-large',
        'global-br-r-xlarge',
        'global-br-r-2xlarge',
        'global-br-r-max'
      ]
    }
  ];

  getSelectedCategoryClasses(): string[] {
    const category = this.classCategories.find(c => c.name === this.selectedCategory);
    return category ? category.classes : [];
  }

  onCategoryChange() {
    this.selectedClass = '';
  }

  onClassSelect() {
    if (this.selectedClass) {
      this.examples.unshift({
        className: this.selectedClass,
        text: this.customText || this.defaultText
      });
      this.selectedClass = '';
      this.customText = '';
    }
  }

  addClass() {
    if (this.manualClass) {
      this.examples.unshift({
        className: this.manualClass,
        text: this.customText || this.defaultText
      });
      this.manualClass = '';
      this.customText = '';
    }
  }

  removeExample(index: number) {
    this.examples.splice(index, 1);
  }
}

